INSERT INTO inclusive (id, insteadOf, say, because, locale, approved, base_id, author_id, categories, links, deleted) VALUES ('01F5ZN19355697ZWP2Y7N7718T', 'patronat', 'genetriat|matronat|opieka', 'słowo patron pochodzi od łacińskiej nazwy ojca. Organizacje feministyczne często używają, dla walki z męską dominacją jezykową, słowa matronat, pochodzącego od matki. Neutralnym płciowo określeniem byłby genetriat, pochodzący od łacińskich słów genitor/genetrix, oznaczających rodziciela/rodzicielkę', 'pl', 1, null, null, 'feminizm', '[]', 0);
