INSERT INTO inclusive (id, insteadOf, say, because, locale, approved, base_id, author_id, categories, links, deleted) VALUES ('01F5ZNV76V1SA8WZ2TH0GQE396', 'zmiana płci', 'korekta płci|tranzycja', 'płci się nie "zmienia", jedynie koryguje się ciało do oczekiwanego wyglądu. Nie tylko osoby trans korygują swoją płeć, powiększanie piersi czy wydłużanie prącia to zabiegi korekty płci popularne wśród osób cispłciowych', 'pl', 1, null, null, 'transpłciowość', '[]', 0);
